import { useState, useRef } from "react";
import { Link } from "react-router-dom";



class Ingredient extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      ingredient: null,
      isLoading: false,
      url: "https://www.themealdb.com/api/json/v1/1/",
    };
    this.inputRef = React.createRef();
  }

  componentDidMount() {
    const { ingredient } = this.props;
    this.setState({ isLoading: true });
    fetch(this.state.url + "filter.php?i=" + ingredient)
      .then((res) => res.json())
      .then((data) => {
        this.setState({ ingredient: data.meals });
        this.setState({ isLoading: false });
      });
  }

  filtreIngrdients = () => {
    const searchValue = this.inputRef.current.value;
    const filteredIngredients = this.state.ingredient.filter((ingredient) => {
      return ingredient.strIngredient
        .toLowerCase()
        .includes(searchValue.toLowerCase());
    });
    this.setState({ ingredient: filteredIngredients });
  };

  render() {
    const { ingredient, isLoading } = this.state;
    return (
      <section className="section main-container">
        <h1 className="title-3d">Search for Ingredient</h1>
        {isLoading ? (
          <div className="ingredients-container">
            <input
              type="search"
              className="search-bar"
              ref={this.inputRef}
              onInput={this.filtreIngrdients}
            />

            {this.inputRef.current &&
              this.inputRef.current.value.length > 0 && (
                <div className="ingredients">
                  {ingredient.map((ingredient, index) => (
                    <div key={index} className="ingredient-item">
                      <Link
                        to={`/${ingredient.strIngredient}`}
                        className="ingredient-link"
                      >
                        {ingredient.strIngredient}
                      </Link>
                    </div>
                  ))}
                </div>
              )}
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </section>
    );
  }
}

export default Ingredient;
