import { useState, useEffect, useRef } from 'react';

const Ingredients = () => {
  const [ingredients, setIngredients] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [url, setUrl] = useState('https://www.themealdb.com/api/json/v1/1/');
  const inputRef = useRef();

  useEffect(() => {
    fetch(url+'list.php?i=list').then(res => res.json()).then(data => {
      setIngredients(data.meals);
      setIsLoading(true);
    })

    const timer = setTimeout(() => {
      if (inputRef.current.value === '') {
        return;
      }
      setUrl('https://www.themealdb.com/api/json/v1/1/search.php?s=' + inputRef.current.value);
    }
    , 500);
  }, [isLoading, url]);

  return (
    <div>
      <h1>Ingredients</h1>
      {isLoading ? (
        <>
          <input type="search" className="search-bar" ref={inputRef} />

          <div className="result-container">
            <div className="result">
              {ingredients.map((ingredient, index) => (
                <div key={index} className="result-item">
                  <p>{ingredient.strIngredient}</p>
                </div>
              ))}
            </div>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  )
}

export default Ingredients;
