import { useState, useEffect, useRef } from 'react';

const Ingredients = () => {
  const [ingredients, setIngredients] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [url, setUrl] = useState('https://www.themealdb.com/api/json/v1/1/list.php?i=list');
  const inputRef = useRef();

  useEffect(() => {
    fetch(url).then(res => res.json()).then(data => {
      setIngredients(data.meals);
      setIsLoading(true);
    })
  }, [isLoading, url])

  // create a search bar that will propose ingredietns with autocomplete
  const handleSearch = () => {
    const search = inputRef.current.value;
    setUrl(`https://www.themealdb.com/api/json/v1/1/search.php?s=${search}`);
  }




  return (
    <div>
      <h1>Ingredients</h1>
      {isLoading ? (
        <input type="text" placeholder="Search for an ingredient" ref={inputRef} onChange={handleSearch} />



      ) : (
        <p>Loading...</p>
      )}
    </div>
  )
}

export default Ingredients;
