import { useState, useEffect, useRef } from 'react';

const Ingredients = () => {
  const [ingredients, setIngredients] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [meals, setMeals] = useState([]);
  const [url, setUrl] = useState('https://www.themealdb.com/api/json/v1/1/');
  const inputRef = useRef();

  useEffect(() => {
    fetch(url+'list.php?i=list').then(res => res.json()).then(data => {
      setIngredients(data.meals);
      setIsLoading(true);
    })

    fetch(url+'search.php?s=').then(res => res.json()).then(data => {
      setMeals(data.meals);
      setIsLoading(true);
    })
  }, [isLoading, url]);

  // get all ingredients from setMeals 


  return (
    <div>
      <h1>Ingredients</h1>
      {isLoading ? (
        <>
          <input type="search" className="search-bar" ref={inputRef} />

          <div className="result-container">
            <div className="result">
              {meals.map((meals, index) => (
                <div key={index} className="result-item">
                  <p>{meals.strMeal}</p>
                </div>
              ))}
            </div>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  )
}

export default Ingredients;
