import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const Ingredient = () => {
  const [isLoading, setIsLoading] = useState(false);
  const { meal } = useParams();
  const [mealData, setMealtData] = useState([]);
  const url = 'https://www.themealdb.com/api/json/v1/1/search.php?s=';

  useEffect(() => {
    fetch(url + `${meal}`)
      .then(res => res.json())
      .then(data => {
        setMealtData(data.meals[0]);
        setIsLoading(true);
      })
  }, [meal]);

  //strIngredient + i and strMeasure + i 

  console.log(mealData)

  return (
    <section className="meal-page section">
      {isLoading ? (
        <>
          <h1 className='title-3d'>{meal}</h1>

          <div className="meal-infos-container">
            <img src={mealData.strMealThumb} alt="" />
            <div className="meal-infos-methode">
              <h2>Category: {mealData.strCategory}</h2>
              <p>{mealData.strInstructions}</p>
            </div>
          </div>

          <div className="meal-ingredient-container">
            <h2>Ingredients</h2>
            </div>
          </>
          ) : <p>Loading...</p>}
        </section>
      );
}

      export default Ingredient;
